mapper.update:
update TB_PERMISSION
DESCRIPTION = #{description,jdbcType=VARCHAR}, PM_ID = #{pmId,jdbcType=CHAR}, PM_NAME = #{pmName,jdbcType=VARCHAR}, PM_TYPE = #{pmType,jdbcType=CHAR}, RULE = #{rule,jdbcType=VARCHAR}, PARENT_ID = #{parentId,jdbcType=CHAR}, IS_LEAF = #{isLeaf,jdbcType=CHAR}, WEIGHT = #{weight,jdbcType=BIGINT}, ICON = #{icon,jdbcType=VARCHAR}
where ID = #{id,jdbcType=INTEGER}

mapper.select:
    	select  from TB_PERMISSION
		
    	1=1
    		
    			and PM_NAME = #{pmName,jdbcType=VARCHAR}
    		
    		
    			and PM_TYPE = #{pmType,jdbcType=CHAR}
    		
    		
    			and PARENT_ID = #{parentId,jdbcType=CHAR}
    		
    		
    			and IS_LEAF = #{isLeaf,jdbcType=CHAR}
    		
    		
   		order by WEIGHT
